void guiShowPage(unsigned pageIndex)
{
	// Hide all pages
	gwinHide(mainContainer);

	// Show page selected page
	switch (pageIndex) {
	case 0:
		gwinShow(mainContainer);
		break;

	default:
		break;
	}
}

